CREATE TRIGGER "SAPHE4"."/1LT/00000862INS" AFTER INSERT ON "SAPHE4"."MAKT" REFERENCING NEW ROW NEW FOR EACH ROW 
BEGIN /* UTC_TIMESTAMP 20171005073734*/ IF ( :NEW.MANDT = '400' ) THEN INSERT INTO "SAPHE4"."/1CADMC/00000862" ( "IUUC_SEQUENCE" , "IUUC_TIMESTAMP" , "IUUC_PROCESSED" , "MANDT" , "MATNR" , "SPRAS" , "IUUC_OPERAT_FLAG"  ) VALUES (  "SAPHE4"."SEQ_/1CADMC/00000862".NEXTVAL, to_char(CURRENT_UTCTIMESTAMP, 'yyyymmddhh24missFF7'), 0, :NEW."MANDT",  :NEW."MATNR",  :NEW."SPRAS",   'I'); END IF; END
