CREATE TRIGGER "SAPHE4"."/1LT/00000862UPD1" AFTER UPDATE ON "SAPHE4"."MAKT" REFERENCING OLD ROW OLD, NEW ROW NEW FOR EACH ROW 
BEGIN /* UTC_TIMESTAMP 20171005073734*/ IF ( :NEW.MANDT = '400' ) THEN IF ( :NEW."MANDT" <> :OLD."MANDT"  OR :NEW."MATNR" <> :OLD."MATNR"  OR :NEW."SPRAS" <> :OLD."SPRAS" ) THEN INSERT INTO "SAPHE4"."/1CADMC/00000862" ( "IUUC_SEQUENCE" , "IUUC_TIMESTAMP" , "IUUC_PROCESSED" , "MANDT" , "MATNR" , "SPRAS" , "IUUC_OPERAT_FLAG"  ) VALUES (  "SAPHE4"."SEQ_/1CADMC/00000862".NEXTVAL, to_char(CURRENT_UTCTIMESTAMP, 'yyyymmddhh24missFF7'), 0, :OLD."MANDT",  :OLD."MATNR",  :OLD."SPRAS",   'D'); INSERT INTO "SAPHE4"."/1CADMC/00000862" ( "IUUC_SEQUENCE" , "IUUC_TIMESTAMP" , "IUUC_PROCESSED" , "MANDT" , "MATNR" , "SPRAS" , "IUUC_OPERAT_FLAG"  ) VALUES (  "SAPHE4"."SEQ_/1CADMC/00000862".NEXTVAL, to_char(CURRENT_UTCTIMESTAMP, 'yyyymmddhh24missFF7'), 0, :NEW."MANDT",  :NEW."MATNR",  :NEW."SPRAS",   'I'); ELSE INSERT INTO "SAPHE4"."/1CADMC/00000862" ( "IUUC_SEQUENCE" , "IUUC_TIMESTAMP" , "IUUC_PROCESSED" , "MANDT" , "MATNR" , "SPRAS" , "IUUC_OPERAT_FLAG"  ) VALUES (  "SAPHE4"."SEQ_/1CADMC/00000862".NEXTVAL, to_char(CURRENT_UTCTIMESTAMP, 'yyyymmddhh24missFF7'), 0, :NEW."MANDT",  :NEW."MATNR",  :NEW."SPRAS",   'U'); END IF; END IF; END
