CREATE TRIGGER "SAPHE4"."/1LT/00003030INS" AFTER INSERT ON "SAPHE4"."MARC" REFERENCING NEW ROW NEW FOR EACH ROW 
BEGIN /* UTC_TIMESTAMP 20171005073808*/ IF ( :NEW.MANDT = '400' ) THEN INSERT INTO "SAPHE4"."/1CADMC/00003030" ( "IUUC_SEQUENCE" , "IUUC_TIMESTAMP" , "IUUC_PROCESSED" , "MANDT" , "MATNR" , "WERKS" , "IUUC_OPERAT_FLAG"  ) VALUES (  "SAPHE4"."SEQ_/1CADMC/00003030".NEXTVAL, to_char(CURRENT_UTCTIMESTAMP, 'yyyymmddhh24missFF7'), 0, :NEW."MANDT",  :NEW."MATNR",  :NEW."WERKS",   'I'); END IF; END
