CREATE TRIGGER "SAPHE4"."/1LT/00003030UPD1" AFTER UPDATE ON "SAPHE4"."MARC" REFERENCING OLD ROW OLD, NEW ROW NEW FOR EACH ROW 
BEGIN /* UTC_TIMESTAMP 20171005073808*/ IF ( :NEW.MANDT = '400' ) THEN IF ( :NEW."MANDT" <> :OLD."MANDT"  OR :NEW."MATNR" <> :OLD."MATNR"  OR :NEW."WERKS" <> :OLD."WERKS" ) THEN INSERT INTO "SAPHE4"."/1CADMC/00003030" ( "IUUC_SEQUENCE" , "IUUC_TIMESTAMP" , "IUUC_PROCESSED" , "MANDT" , "MATNR" , "WERKS" , "IUUC_OPERAT_FLAG"  ) VALUES (  "SAPHE4"."SEQ_/1CADMC/00003030".NEXTVAL, to_char(CURRENT_UTCTIMESTAMP, 'yyyymmddhh24missFF7'), 0, :OLD."MANDT",  :OLD."MATNR",  :OLD."WERKS",   'D'); INSERT INTO "SAPHE4"."/1CADMC/00003030" ( "IUUC_SEQUENCE" , "IUUC_TIMESTAMP" , "IUUC_PROCESSED" , "MANDT" , "MATNR" , "WERKS" , "IUUC_OPERAT_FLAG"  ) VALUES (  "SAPHE4"."SEQ_/1CADMC/00003030".NEXTVAL, to_char(CURRENT_UTCTIMESTAMP, 'yyyymmddhh24missFF7'), 0, :NEW."MANDT",  :NEW."MATNR",  :NEW."WERKS",   'I'); ELSE INSERT INTO "SAPHE4"."/1CADMC/00003030" ( "IUUC_SEQUENCE" , "IUUC_TIMESTAMP" , "IUUC_PROCESSED" , "MANDT" , "MATNR" , "WERKS" , "IUUC_OPERAT_FLAG"  ) VALUES (  "SAPHE4"."SEQ_/1CADMC/00003030".NEXTVAL, to_char(CURRENT_UTCTIMESTAMP, 'yyyymmddhh24missFF7'), 0, :NEW."MANDT",  :NEW."MATNR",  :NEW."WERKS",   'U'); END IF; END IF; END
